#include<iostream>#include<fstream>#include<cmath>#include<cstdlib>using namespace std;#define W 3300#define H 3000int a,b,i,go,last,k;double X,Y,x,y,x2,y2;char s;ofstream tga;int main(void){	tga.open("MandelbrotSet.tga",ofstream::binary);	s=0;tga.put(s);s=0;tga.put(s);	s=2;tga.put(s);	i=0;tga.put(i%256);tga.put(i/256);	i=0;tga.put(i%256);tga.put(i/256);	s=0;tga.put(s);	i=0;tga.put(i%256);tga.put(i/256);	i=0;tga.put(i%256);tga.put(i/256);	tga.put(W%256);tga.put(W/256);	tga.put(H%256);tga.put(H/256);	s=24;tga.put(s);s=0x20;tga.put(s);	last=1000;	for(b=H-1;b>=0;b-=1)	{		if(100*int(b/100)==b)cout<<b<<"\n";		for(a=0;a<=W-1;a++)		{			k=0;			X=1.25*(2.0*a-1.5*W)/(1.0*H);x=X;			Y=1.25*(2.0*b-1.0*H)/(1.0*H);y=Y;			for(go=1;go<=last;go++)			{				x2=x*x;y2=y*y;y=2.0*x*y+Y;x=x2-y2+X;				if(x2+y2>4.0)break;k++;			}			if(k==last)i=255;else				i=int(255.0*pow(1.0/cosh(-0.02*k),2));			tga.put(i);tga.put(i);tga.put(i);		}	}	tga.close();cout<<"File written\n";	return 0;}