#include <iostream>#include <fstream>#include <cmath>#include <cstdlib>using namespace std;#define N 10000int myrandom[N+1];int main (void){	srand ( time(NULL) );	cout << "The largest random number that this computer produces is "<<RAND_MAX<<"\n";	int mynumber=1;	for(int i=0;i<=N;i++)myrandom[i]=rand();	for(;;)	{		int swap=0;		for(int i=0;i<=N-1;i++)		{			if(myrandom[i]<myrandom[i+1])			{				swap++;				int mytemp=myrandom[i];				myrandom[i]=myrandom[i+1];				myrandom[i+1]=mytemp;			}		}		if(swap==0)break;	}	cout<<"\n";	for(int i=0;i<=10;i++)cout<<myrandom[i]<<"\n";	for(int i=0;i<=3;i++)cout<<".\n";	for(int i=N-10;i<=N;i++)cout<<myrandom[i]<<"\n";	system("PAUSE");//Could use this for PC, but not part of C++ language		return 0;}