#include <iostream>#include <fstream>#include <cmath>#include <cstdlib>using namespace std;double MyRandom(void);void NewCoords(void);double x,y,z,Lastx,Lasty,Lastz,q;int particle;ofstream Julia("Brownian Motion.dxf");int main(void){q=0.3;Julia<<"0\nSECTION\n2\nENTITIES\n";for(particle=0;particle<=60;particle++){Lastx=MyRandom();Lasty=MyRandom();Lastz=MyRandom();for(;;){NewCoords();if(x>0.5||x<-0.5||y>0.5||y<-0.5||z>0.5||z<-0.5)break;Julia<<"0\nLINE\n8\n0\n";Julia<<"10\n"<<Lastx<<"\n";Julia<<"20\n"<<Lasty<<"\n";Julia<<"30\n"<<Lastz<<"\n";Julia<<"11\n"<<x<<"\n";Julia<<"21\n"<<y<<"\n";Julia<<"31\n"<<z<<"\n";Julia<<"62\n"<<particle<<"\n";Lastx=x;Lasty=y;Lastz=z;}}Julia<<"0\nENDSEC\n0\nEOF\n";Julia.close();cout<<"Finished\n";return 0;}double MyRandom(void){return rand()/32768.0-0.5;}void NewCoords(void){x=Lastx+q*MyRandom();y=Lasty+q*MyRandom();z=Lastz+q*MyRandom();}