#include <iostream>#include <fstream>#include <cmath>#include <cstdlib>using namespace std;void DXFSetUp(void);void DXFLines(void);void DXFSurface(void);void DXFFinishOff(void);int Layer,Colour;float DXFx1,DXFy1,DXFz1,      DXFx2,DXFy2,DXFz2,      DXFx3,DXFy3,DXFz3,      DXFx4,DXFy4,DXFz4;ofstream Chris("Little.dxf");int main(void){DXFSetUp();Layer=0;Colour=0;DXFx1=+800.0;DXFy1=+456.9;DXFz1=+123.4;DXFx2=+125.0;DXFy2=+425.9;DXFz2=+137.4;DXFLines();DXFx1=-800.0;DXFy1=-866.9;DXFz1=+903.4;DXFx2=+333.0;DXFy2=-890.9;DXFz2=-673.4;DXFx3=+555.5;DXFy3=+678.2;DXFz3=+156.2;DXFx4=-499.0;DXFy4=+231.1;DXFz4=+890.3;DXFSurface();DXFFinishOff();cout<<"DXF file written, end of program\n";return 0;}void DXFSetUp(void){Chris<<"0\n";Chris<<"SECTION\n";Chris<<"2\n";Chris<<"ENTITIES\n";}void DXFLines(void){Chris<<"0\nLINE\n8\n"<<Layer<<"\n";Chris<<"10\n"<<DXFx1<<"\n";Chris<<"20\n"<<DXFy1<<"\n";Chris<<"30\n"<<DXFz1<<"\n";Chris<<"11\n"<<DXFx2<<"\n";Chris<<"21\n"<<DXFy2<<"\n";Chris<<"31\n"<<DXFz2<<"\n";Chris<<"62\n"<<Colour<<"\n";}void DXFSurface(void){Chris<<"0\n3DFACE\n8\n"<<Layer<<"\n";Chris<<"10\n"<<DXFx1<<"\n";Chris<<"20\n"<<DXFy1<<"\n";Chris<<"30\n"<<DXFz1<<"\n";Chris<<"11\n"<<DXFx2<<"\n";Chris<<"21\n"<<DXFy2<<"\n";Chris<<"31\n"<<DXFz2<<"\n";Chris<<"12\n"<<DXFx3<<"\n";Chris<<"22\n"<<DXFy3<<"\n";Chris<<"32\n"<<DXFz3<<"\n";Chris<<"13\n"<<DXFx4<<"\n";Chris<<"23\n"<<DXFy4<<"\n";Chris<<"33\n"<<DXFz4<<"\n";Chris<<"62\n"<<Colour<<"\n";}void DXFFinishOff(void){Chris<<"0\n";Chris<<"ENDSEC\n";Chris<<"0\n";Chris<<"EOF\n";Chris.close();}